text-decoration:none;cursor:pointer}.footer__links a:link{color:#fff}.footer__links a:hover{color:#fff}.footer__links a:visited{color:#fff}.footer__links a:active{color:#fff}@media (min-width:600px){.footer__links a{font-size:.875rem;font-size:var(--size-service__body)}}.footer__small-reference{grid-area:small;display:none;text-align:center;font-size:.75rem;font-size:var(--size-navbar__link);align-self:flex-end}@media (min-width:600px) and (max-width:1199px){.footer__small-reference{display:block}}.footer__big-reference{grid-area:big;display:none}@media (min-width:1200px){.footer__big-reference{display:block}}.footer__big-reference__title{font-family:relawey-heavy,Quicksand,Source Sans Pro,-apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,sans-serif;font-weight:700;font-size:1.5rem}.footer__big-reference__subtitle{font-size:.75rem;font-size:var(--size-section__subtitle)}.footer__social{grid-area:social;display:none;font-size:1.5rem}.footer__social a{text-decoration:none;cursor:pointer;color:#fff}.footer__social a:not(:last-child){margin-right:1rem}.footer__social a:link{color:#fff}.footer__social a:hover{color:#fff}.footer__social a:visited{color:#fff}.footer__social a:active{color:#fff}@media (min-width:1200px){.footer__social{display:-webkit-flex;display:flex;justify-self:flex-end}}</style>
